import './App.css';
import Header from './components/Header.';
import Sidebar from './components/Sidebar';
import CreatePost from './components/CreatePost';
import Explore from './components/Explore';
import { useState } from 'react';

function App() {
  async function getTweets(){
    const [tweets, settweets] = useState([])
    let rawData = await fetch("https://apex.oracle.com/pls/apex/mayank281/tweets/get");
    let data = await rawData.json();
    settweets(data);
    console.log(tweets);
  }
  return (
    <>
      <div className="container">
        <Header />
        <div className="row" style={{margin: -6}}>
          <Sidebar />
          <div className="col-6 border border-top-0 border-bottom-0">
            <CreatePost />

            <div className="row border-bottom mt-2">
              <div className="col-1 fs-1 p-3 pt-0 d-flex justify-content-start">
                <i className="bi bi-person-circle"></i>
              </div>
            <div className="col-11 ps-4">
                <div className="row fw-bold d-flex">
                  New User
                  <i className="bi bi-three-dots w-auto ms-auto"></i>
                </div>
                <div className="row">Lorem ipsum dolor sit amet consectetur adipisicing elit. Soluta, voluptas. Lorem ipsum dolor sit amet.</div>
                <div className="row mt-2 mb-2">
                  <div className="col fs-5 text-secondary"><i className="bi bi-chat"></i></div>
                  <div className="col fs-5 text-secondary"><i className="bi bi-repeat"></i></div>
                  <div className="col fs-5 text-secondary"><i className="bi bi-heart"></i></div>
                  <div className="col fs-5 text-secondary"><i className="bi bi-upload"></i></div>
                </div>
            </div>
            </div>

          </div>
          <Explore />
        </div>
      </div>
    </>
  );
}

export default App;
