import logo from './logo.svg';
import './App.css';
import Header from './components/Header.';
import Sidebar from './components/Sidebar';

function App() {
  return (
    <>
      <div className="container">
        <Header />
        <div className="row" style={{margin: -6}}>
          <Sidebar />
          <div className="col-6 border border-top-0 border-bottom-0">
            <div className="row border-bottom">
              <div className="col-1 fs-1 p-3 pt-0 d-flex justify-content-start"><i class="bi bi-person-circle"></i></div>
              <div className="col-11">
                <textarea type="text" placeholder="What's happening?" className='w-100 border-0 p-3' style={{border: 'none', outline:'none'}} ></textarea>
                <button type="button" className="btn btn-primary offset-10 p-2 mt-2 mb-3 rounded-5" style={{width: 70, height:45}} ><p className='fw-semibold'>Tweet</p></button>
              </div>
            </div>
          </div>
          <div className="col-3">hello in 3</div>
        </div>
      </div>
    </>
  );
}

export default App;
